import './App.css';
import { useState, useRef } from 'react';
import { Chessboard } from 'react-chessboard';
import { Chess } from 'chess.js'
import useWindowDimensions from './util/useWindowDimensions';

function App() {
  const _window = useWindowDimensions();

  const [game, setGame] = useState(new Chess());
  const [currentTimeout, setCurrentTimeout] = useState();
  const chessboardRef = useRef(null);

  function safeGameMutate(modify) {
    setGame((g) => {
      const update = Object.assign(Object.create(Object.getPrototypeOf(g)), g);
      modify(update);
      return update;
    });
  }

  function oppTurn() {
    safeGameMutate((g) => {
      g.move({from: "a7", to: "a5"});
    })
  }

  function onDrop(src, dst) {
    // gameCopy is deep copy that maintains prototype
    let gameCopy = Object.assign(Object.create(Object.getPrototypeOf(game)), game);
    console.log(src, dst)
    const move = gameCopy.move({
      from: src,
      to: dst,
      promotion: "q",
    });
    if(move === null) return false;

    setGame(gameCopy)
    const newTimeout = setTimeout(oppTurn, 400);
    setCurrentTimeout(newTimeout);

    return true;
  }

  return (
    <div className='App flex justify-center bg-slate-700'>
      <div className='MenuWrapper border-red-400'>
        <button className='ResetButton'
          onClick={() => {
            safeGameMutate(g => g.undo());
            chessboardRef.current?.clearPremoves();
            clearTimeout(currentTimeout);
          }}
        >
        </button>
        <button className='UndoButton'
          onClick={() => {
            safeGameMutate(g => g.reset());
            chessboardRef.current?.clearPremoves();
            clearTimeout(currentTimeout);
          }}
        >
        </button>
      </div>
    </div>
  );
}

export default App;
