import './App.css';
import { useState, useRef } from 'react';
import { Chessboard } from 'react-chessboard';
import { Chess } from 'chess.js'
import useWindowDimensions from './util/useWindowDimensions';

function App() {
  const _window = useWindowDimensions();

  const [game, setGame] = useState(new Chess());

  function opponentTurn(modify) {
    setGame((g) => {
      let gameCopy = Object.assign(Object.create(Object.getPrototypeOf(game)), game);
      modify(gameCopy);
      return gameCopy;
    })
  }

  function onDrop(src, dst) {
    // gameCopy is deep copy that maintains prototype
    let gameCopy = Object.assign(Object.create(Object.getPrototypeOf(game)), game);
    const move = gameCopy.move({
      from: src,
      to: dst,
      promotion: "q",
    });
    console.log(move)
    setGame(gameCopy)
    return false
  }

  return (
    <div className='App flex justify-center bg-slate-700'>
      <div className='ChessboardWrapper'>
        <Chessboard
          id='chessBoard'
          position={game.fen()}
          onPieceDrop={onDrop}
          arePremovesAllowed={true}
          boardWidth={_window.height}
          customBoardStyle={{
            borderRadius: "4px",
            boxShadow: "0 2px 10px rgba(0, 0, 0, 0.5)",
          }}
          customDarkSquareStyle={{ backgroundColor: "#779952" }}
          customLightSquareStyle={{ backgroundColor: "#edeed1" }}

        />
      </div>
    </div>
  );
}

export default App;
