import './App.css';
import { useState } from 'react';
import { Chessboard } from 'react-chessboard'
import { Chess } from 'chess.js';
import useWindowDimensions from './components/util/useWindowDimensions'

const App = () => {
  const { width, height } = useWindowDimensions();
  const [boardPosition, setBoardPosition] = useState(null);
  const handleBoardPosition = (e) => {
    setBoardPosition(e);
    console.log(e);
  }
  const [game, setGame] = useState(new Chess)

  // Chessjs specific api function
  const safeGameMutate = (modify) => {
    setGame((g) => {
      const update = { ...g };
      modify(update);
      return update;
    });
  }

  const onDrop = (sourceSquare, targetSquare) => {
    const gameCopy = { ...game };
    const move = gameCopy.move({
      from: sourceSquare,
      to: targetSquare,
      promotion: "q", // always promote to a queen for example simplicity
    });
    setGame(gameCopy);
    return move;
  }

  const pieces = ["wP", "wN", "wB", "wR", "wQ", "wK", "bP", "bN", "bB", "bR", "bQ", "bK"];
  const customPieces = () => {
    const returnPieces = {};
    pieces.map((p) => {
      returnPieces[p] = ({ squareWidth }) => (
        <div
          style={{
            width: squareWidth,
            height: squareWidth,
            backgroundImage: `url(/${p}.png)`,
            backgroundSize: "100%",
          }}
        />
      );
      return null;
    });
    return returnPieces;
  };

  return (
    <div className='App flex justify-center bg-slate-700'>
      <div className='ChessboardWrapper'>
        <Chessboard
          boardWidth={height}
          getPositionObject={handleBoardPosition}

        />
      </div>
    </div>
  );
}

export default App;
