import '../App.css';

export default function SideMenu(props) {
  return(
    <>
      <div className='MenuWrapper'>
        <button className='ResetButton rounded-sm bg-slate-800 px-2'
          onClick={() => {
            props.safeGameMutate(g => g.undo());
            props.chessboardRef.current?.clearPremoves();
            clearTimeout(props.currentTimeout);
          }}
        > Reset
        </button>
        <button className='UndoButton rounded-sm bg-gray-50 px-2'
          onClick={() => {
            props.safeGameMutate(g => g.reset());
            props.chessboardRef.current?.clearPremoves();
            clearTimeout(props.currentTimeout);
          }}
        > Undo
        </button>
      </div>
    </>
  )
}
