          boardOrientation={color}
          onPieceDrop={onDrop}
          boardWidth={height}
          getPositionObject={handleBoardPosition}
          customBoardStyle={{
            borderRadius: "4px",
            boxShadow: "0 2px 10px rgba(0, 0, 0, 0.5)",
          }}
          customDarkSquareStyle={{ backgroundColor: "#779952" }}
          customLightSquareStyle={{ backgroundColor: "#edeed1" }}

        />
      </div>
    </div>
  );
}

export default App;
