import './App.css';
import { useEffect, useState } from 'react';
import { Chessboard } from 'react-chessboard';
import { Chess } from 'chess.js';
import useWindowDimensions from './components/util/useWindowDimensions';

function App() {
  const _window = useWindowDimensions();

  const [game, setGame] = useState(new Chess());
  useEffect(()=>{
    setGame(new Chess())
  }, [])

  const [currentTimeout, setCurrentTimeout] = useState(null);

  function safeGameMutate(modify) {
    setGame((g) => {
      const update = { ...g };
      modify(update);
      return update;
    });
  }

  function makeRandomMove() {
    const possibleMoves = game.moves();
    if (game.game_over() || game.in_draw() || possibleMoves.length === 0) return;

    const randomIndex = Math.floor(Math.random() * possibleMoves.length);
    safeGameMutate((game) => {
      game.move(possibleMoves[randomIndex]);
    });
  }

  function onDrop(srcSqr, dstSqr) {
    const gameCopy = { ...game };
    const move = gameCopy.move({
      from: srcSqr,
      to: dstSqr,
      promotion: "q", // always promote to a queen for example simplicity
    });
    setGame(gameCopy);

    // illegal move
    if (move === null) return false;

    // store timeout so it can be cleared on undo/reset so computer doesn't execute move
    const newTimeout = setTimeout(makeRandomMove, 2000);
    setCurrentTimeout(newTimeout);
    return true;
  }

  return (
    <div className='App flex justify-center bg-slate-700'>
      <div className='ChessboardWrapper'>
        <Chessboard
          id='chessBoard'
          arePremovesAllowed={true}
          onPieceDrop={onDrop}
          position={game.fen()}
          isDraggablePiece={({ piece }) => piece[0] === "w"}
          boardWidth={_window.height}
          customBoardStyle={{
            borderRadius: "4px",
            boxShadow: "0 2px 10px rgba(0, 0, 0, 0.5)",
          }}
          customDarkSquareStyle={{ backgroundColor: "#779952" }}
          customLightSquareStyle={{ backgroundColor: "#edeed1" }}

        />
      </div>
    </div>
  );
}

export default App;
